mapper.select:
SELECT COUNT(*)
FROM MEMBER
WHERE MEMBER_NO = ${memberNo}

SELECT MEMBER_EMAIL
FROM MEMBER
WHERE MEMBER_NAME = #{memberName}
AND MEMBER_TEL = #{memberTel}

mapper.update:
UPDATE MEMBER SET
MEMBER_PW = #{memberPw}
WHERE MEMBER_EMAIL = #{memberEmail}

mapper.select:
SELECT MEMBER_TEL
FROM MEMBER
WHERE MEMBER_TEL = #{memberTel}

mapper.update:
UPDATE MEMBER SET
    KAKAO_ACCESS_TOKEN = #{kakaoAccessToken}
WHERE MEMBER_EMAIL = #{memberEmail}
