SELECT COUNT(*)
FROM ILCHON
WHERE TO_MEMBER_NO = #{toMemberNo}
AND FROM_MEMBER_NO = #{fromMemberNo}

SELECT COUNT(*)
FROM TODAY
WHERE HOME_NO = #{homeNo}
AND VISIT_NO = #{visitNo}
AND VISIT_DATE = TRUNC(SYSDATE)

SELECT COUNT(*)
FROM TODAY
WHERE HOME_NO = #{homeNo}

SELECT COUNT(*)
FROM ILCHON 
WHERE FROM_MEMBER_NO = #{fromMemberNo} 
AND TO_MEMBER_NO = #{toMemberNo}
AND IS_ILCHON = 'N'

SELECT COUNT(*)
FROM ILCHON 
WHERE FROM_MEMBER_NO = #{fromMemberNo} 
AND TO_MEMBER_NO = #{toMemberNo}
AND IS_ILCHON = 'Y'
